import React, { useState } from 'react'
import axios from 'axios'
import './App.css'

const baseUrl = 'http://localhost:8060/'

function App() {
  const [url, setUrl] = useState('')
  const initApiResponse = {
    status: '',
    message: '',
    streamStatus: '',
  }
  const [apiResponse, setApiResponse] = useState(initApiResponse)
  const errorReference = {
    url_required: '請輸入想要查詢Youtube直播網址',
    unsupported_url: '網址錯誤或非Youtube網址',
    api_error: '發生錯誤',
  }

  const handleUrlChange = (event) => {
    setUrl(event.target.value)
  }

  async function CheckStreamStatus(url) {
    await axios.get(
      `${baseUrl}api/stream/status/`,
      {
        params: {
          url,
        }
      }
    ).then((response) => {
      setApiResponse({
        status: response.data.status,
        streamStatus: response.data.data.stream_status,
      })
    }).catch((error) => {
      setApiResponse({
        status: error.response.data.status,
        message: error.response.data.message,
      })
    })
  }
  console.log(apiResponse.streamStatus)

  const handleCheckStatus = () => {
    CheckStreamStatus(url)
  }


  return (
    <div className="App">
      <div>
        <label>
          Enter URL:
          <input type="text" value={url} onChange={handleUrlChange} />
        </label>
        <button onClick={handleCheckStatus}>Check Status</button>

        {apiResponse && (
          <div>
            {apiResponse.status ? (
              <p>Data: {apiResponse.streamStatus}</p>
            ) : (
              <p>Error: {apiResponse.message}</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
